import logo from './logo.svg';
import './App.css';
import './css/global.css';
import './css/custom.css';
import './css/colors.css';
import WeatherApp from './components/weather-calculator';
import FormApp from './components/form';
import Header from './components/header';

const API_KEY = 'ede8080f6378fcfb399d37596d75fdcf';


class App extends Component {
  getWeather = async () => {
      const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=Manchester,uk&appid=${API_KEY}&units=metric`);
      const data = await api_call.json();

